mapper.select:



		AND menu_name like concat('%', #{menuName}, '%')


		AND visible = #{visible}


order by parent_id, order_num

SELECT DISTINCT m.menu_id, m.parent_id, m.menu_name, m.url, m.visible, ifnull( m.perms, '' ) AS perms, m.target, m.menu_type, m.icon, m.order_num, m.create_time FROM sys_menu m LEFT JOIN sys_role_menu rm ON m.menu_id = rm.menu_id LEFT JOIN sys_user_role ur ON rm.role_id = ur.role_id LEFT JOIN sys_role ro ON ur.role_id = ro.id WHERE ur.user_id = #{params.userId}
AND m.menu_name like concat('%', #{menuName}, '%')
AND m.visible = #{visible}
order by m.parent_id, m.order_num
